/**
 * Toast Handler
 */
document.addEventListener("DOMContentLoaded", function () {
(
    function () {
    "use strict";
    
    let element = document.querySelector('.toast');
    let success = element.querySelector('.success');
    let error = element.querySelector('.error');
    let body = element.querySelector('.toast-body');
    let toast = new bootstrap.Toast(element);

    function show(type, message){
      success.classList.add("d-none");
      error.classList.add("d-none");
      let header = (type == "success") ? success : error;
      header.classList.remove("d-none");
      body.innerHTML = message;
      toast.show();
    }

    /**
     * Initialise our application's code.
     */
    function init() {

      window.toast = {};
      window.toast.show = show;

      console.log("Toast manager have been initialized")
    }

    /**
     * Run the app.
     */
     init();

  })();
});

